import React, { useEffect, useState } from "react";

function TakenBooks() {
  const [taken, setTaken] = useState([]);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("taken_books") || "[]");
    setTaken(stored);
  }, []);

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ textAlign: "center", color: "#0e6251" }}> Books Taken</h2>
      {taken.length === 0 ? (
        <p style={{ textAlign: "center" }}>No books taken yet.</p>
      ) : (
        <table style={{
          margin: "auto",
          borderCollapse: "collapse",
          width: "80%",
          backgroundColor: "#eefdfc",
          border: "1px solid #ccc"
        }}>
          <thead>
            <tr style={{ backgroundColor: "#d0f0f0" }}>
              <th style={{ padding: "10px", border: "1px solid #ccc" }}>Book</th>
              <th style={{ padding: "10px", border: "1px solid #ccc" }}>Copies</th>
              <th style={{ padding: "10px", border: "1px solid #ccc" }}>Reserved On</th>
            </tr>
          </thead>
          <tbody>
            {taken.map((b, index) => (
              <tr key={index}>
                <td style={{ padding: "10px", border: "1px solid #ccc" }}>{b.title}</td>
                <td style={{ padding: "10px", border: "1px solid #ccc" }}>{b.copies}</td>
                <td style={{ padding: "10px", border: "1px solid #ccc" }}>{new Date(b.reserved_on).toLocaleString()}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default TakenBooks;
