import React, { useEffect, useState } from "react";

function Fees() {
  const [fees, setFees] = useState([]);

  useEffect(() => {
    const storedFees = JSON.parse(localStorage.getItem("due_fees") || "[]");
    setFees(storedFees);
  }, []);

  return (
    <div style={{
      background: "linear-gradient(to right, #f0f5f9, #dfeef5)",
      minHeight: "100vh",
      padding: "50px",
      display: "flex",
      justifyContent: "center",
      alignItems: "center"
    }}>
      <div style={{
        background: "#fff",
        padding: "30px",
        borderRadius: "16px",
        boxShadow: "0 4px 12px rgba(0,0,0,0.1)",
        width: "90%",
        maxWidth: "600px"
      }}>
        <h2 style={{ color: "#2a4d69", textAlign: "center", marginBottom: "20px" }}>
           My Due Fees
        </h2>

        {fees.length === 0 ? (
          <p style={{ textAlign: "center" }}>You have no due fees ðŸŽ‰</p>
        ) : (
          fees.map((fee, index) => (
            <div key={index} style={{
              borderBottom: "1px solid #eee",
              padding: "10px 0"
            }}>
              <p><strong>Book:</strong> {fee.title}</p>
              <p><strong>Amount:</strong> â‚¹{fee.amount}</p>
              <p><strong>Due Date:</strong> {fee.dueDate}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default Fees;
