import React, { useEffect, useState } from "react";
import { getBooks, reserveBook } from "../api/api";

function Books() {
  const [books, setBooks] = useState([]);
  const [copies, setCopies] = useState(1);
  const [amount, setAmount] = useState(0);
  const [days, setDays] = useState(7); // default reservation period

  useEffect(() => {
    const fetchBooks = async () => {
      const res = await getBooks();
      setBooks(res.data);
    };
    fetchBooks();
  }, []);

  const handleReserve = async (book) => {
    const username = JSON.parse(localStorage.getItem("user")).username;

    try {
      const res = await reserveBook(book.id, {
        username,
        copies: parseInt(copies),
        amount: parseInt(amount),
      });

      // Alert
      alert(`‚úÖ "${book.title}" reserved successfully on ${new Date(res.data.reserved_on).toLocaleString()}.\nPay ‚Çπ${amount} within ${days} days.`);

      // Update localStorage
      const dueFees = JSON.parse(localStorage.getItem("due_fees") || "[]");
      const takenBooks = JSON.parse(localStorage.getItem("taken_books") || "[]");

      dueFees.push({
        title: book.title,
        amount: amount,
        dueDate: new Date(res.data.expires_on).toLocaleDateString(),
      });

      takenBooks.push({
        title: book.title,
        copies: copies,
        reserved_on: res.data.reserved_on,
      });

      localStorage.setItem("due_fees", JSON.stringify(dueFees));
      localStorage.setItem("taken_books", JSON.stringify(takenBooks));
    } catch (err) {
      alert("‚ùå Reservation failed. Check copies/amount.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2 style={{ color: "#2a4d69", textAlign: "center" }}>üìö List of Available Books</h2>
      {books.length === 0 && <p style={{ textAlign: "center" }}>No books available.</p>}

      <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}>
        {books.map((book) => (
          <div key={book.id} style={{
            border: "1px solid #ccc",
            borderRadius: "12px",
            padding: "16px",
            margin: "10px",
            width: "300px",
            backgroundColor: "#f4faff",
            boxShadow: "0 2px 6px rgba(0,0,0,0.1)"
          }}>
            <h3 style={{ color: "#0a3d62" }}>{book.title}</h3>
            <p><strong>Author:</strong> {book.author}</p>
            <p><strong>Available:</strong> {book.available_copies} copies</p>

            <input
              type="number"
              min={1}
              max={book.available_copies}
              value={copies}
              onChange={(e) => setCopies(e.target.value)}
              placeholder="Copies"
              style={{ width: "60px", marginRight: "10px" }}
            />

            <input
              type="number"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
              placeholder="Amount ‚Çπ"
              style={{ width: "100px", marginBottom: "8px" }}
            />

            <br />
            <label>
              üìÖ Reserve For:
              <select
                value={days}
                onChange={(e) => setDays(e.target.value)}
                style={{ marginLeft: "10px" }}
              >
                {[3, 5, 7, 10, 14].map(d => (
                  <option key={d} value={d}>{d} days</option>
                ))}
              </select>
            </label>

            <br /><br />
            <button
              onClick={() => handleReserve(book)}
              style={{
                backgroundColor: "#2a4d69",
                color: "white",
                padding: "8px 16px",
                border: "none",
                borderRadius: "8px",
                cursor: "pointer"
              }}
            >
              Reserve
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Books;
