import React, { useEffect, useState } from "react";
import { getBooks, reserveBook } from "../api/api";

function Books() {
  const [books, setBooks] = useState([]);
  const [copies, setCopies] = useState(1);
  const [amount, setAmount] = useState(0);

  useEffect(() => {
    const fetchBooks = async () => {
      const res = await getBooks();
      setBooks(res.data);
    };
    fetchBooks();
  }, []);

  const handleReserve = async (book) => {
    const username = JSON.parse(localStorage.getItem("user")).username;
    try {
      const res = await reserveBook(book.id, { username, copies, amount });
      alert(`"${book.title}" reserved successfully at ${new Date(res.data.reserved_on).toLocaleString()}.\nPlease pay the bill.`);
    } catch {
      alert("Reservation failed.");
    }
  };

  return (
    <div>
      <h2>List of Books</h2>
      {books.map((book) => (
        <div key={book.id} style={{ border: "1px solid #ddd", padding: 10, margin: 10 }}>
          <h4>{book.title}</h4>
          <p>Author: {book.author}</p>
          <p>Available: {book.available_copies}</p>
          <input type="number" placeholder="Copies" value={copies} onChange={(e) => setCopies(e.target.value)} />
          <input type="number" placeholder="Amount" value={amount} onChange={(e) => setAmount(e.target.value)} />
          <button onClick={() => handleReserve(book)}>Reserve</button>
        </div>
      ))}
    </div>
  );
}

export default Books;
